const DATA=data;function emailInputIsValid(input){return input.value!==''&&input.checkValidity()}
function searchPersonByEmail(email){for(let person of DATA){if(person.email===email){return person}}
    return undefined}
function savePerson(person){localStorage.setItem('person',JSON.stringify(person))}
function getCurrentPage(){var locationPath=window.location.pathname;var locationPathArray=locationPath.split('/');return locationPathArray[locationPathArray.length-1]}
function redirectSearchResult(){if(getCurrentPage()==='index.html'){window.location.href="pages/search-result.html"}else{window.location.reload()}}
function searchEmail(input_id){var input=document.getElementById(input_id);if(emailInputIsValid(input)){var person=searchPersonByEmail(input.value);if(person!==undefined){savePerson(person)}else{localStorage.removeItem('person')}
    redirectSearchResult()}}
