function getPerson(){var person=localStorage.getItem('person');person=JSON.parse(person);return person}
function writeTextInElement(elementId,text){var element=document.getElementById(elementId);element.innerText=text}
function writeItemsInHTML(elementId,list,property){var element=document.getElementById(elementId);let HTMLItems='';for(let item of list){HTMLItems+='<p>'+item[property]+'</p>'}
    element.innerHTML=HTMLItems}
function changeDisplay(){document.getElementById('index-content').className='no-display';document.getElementById('search-result').classList.remove('no-display');document.getElementById('person-card').classList.remove('no-display');document.getElementById('no-results').classList.remove('no-display')}
function changeSearchModuleText(){document.getElementById('search-module-title').innerText='Canâ€™t Find The Right Person?';document.getElementById('search-module-subtitle').innerHTML='<strong>Try Again</strong> - Make a new search'}
function writePersonCardData(person){writeTextInElement('person-card-title',person.name+', '+person.age);writeTextInElement('person-card-notes',person.notes);writeTextInElement('person-card-address',person.address);writeTextInElement('person-card-email',person.email);writeItemsInHTML('person-card-phone',person.phoneNumbers,'phone');writeItemsInHTML('person-card-relatives',person.relatives,'name')}
function showSearchResult(){var person=getPerson();if(person!==null){document.getElementById('no-results').classList.add('no-display');writePersonCardData(person)}else{document.getElementById('person-card').classList.add('no-display')}}
function reload(){window.location.reload()}
function changeView(){changeDisplay();changeSearchModuleText();showSearchResult()}
