const person=getPerson();if(person!==null){document.getElementById('no-results').className='no-display';writeTextInElement('person-card-title',person.name+', '+person.age);writeTextInElement('person-card-notes',person.notes);writeTextInElement('person-card-address',person.address);writeTextInElement('person-card-email',person.email);writeItemsInHTML('person-card-phone',person.phoneNumbers,'phone');writeItemsInHTML('person-card-relatives',person.relatives,'name')}else{document.getElementById('person-card').className='no-display'}
function getPerson(){var person=localStorage.getItem('person');person=JSON.parse(person);return person}
function writeTextInElement(elementId,text){var element=document.getElementById(elementId);element.innerText=text}
function writeItemsInHTML(elementId,list,property){var element=document.getElementById(elementId);let HTMLItems='';for(let item of list){HTMLItems+='<p>'+item[property]+'</p>'}
    element.innerHTML=HTMLItems}
